            for (size_t j = 1; j < selected_indices.size(); ++j) {
                current_cost += p[s[selected_indices[j-1]]][s[selected_indices[j]]];
            }

            if (min_cost == -1 || current_cost < min_cost) {
                min_cost = current_cost;
            }
        }
