struct Point {
    int x, w, idx;

    bool operator<(const Point& other) const {
        if (w != other.w) return w < other.w;
        return idx < other.idx;
    }
};

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;

    while (t--) {
        cin >> ws;

        int n, m;
        cin >> n >> m;

        vector<Point> points(m);
        for (int i = 0; i < m; ++i) {
            cin >> points[i].x >> points[i].w;
            points[i].idx = i + 1;
        }

        sort(points.begin(), points.end(), [](const Point& a, const Point& b) {
            return a.x < b.x;
        });

        vector<long long> left_sum(m, LLONG_MAX);
        priority_queue<int> left_pq;
        long long left_current = 0;

        for (int i = 0; i < m; ++i) {
            int w = points[i].w;
            if (left_pq.size() < (size_t)n) {
                left_pq.push(w);
                left_current += w;
            } else {
                if (w < left_pq.top()) {
                    left_current -= left_pq.top();
                    left_pq.pop();
                    left_pq.push(w);
                    left_current += w;
                }
            }
            if (i + 1 >= n) {
                left_sum[i] = left_current;
            }
        }

        vector<long long> right_sum(m, LLONG_MAX);
        priority_queue<int> right_pq;
        long long right_current = 0;

        for (int i = m - 1; i >= 0; --i) {
            int w = points[i].w;
            if (right_pq.size() < (size_t)n) {
                right_pq.push(w);
                right_current += w;
            } else {
                if (w < right_pq.top()) {
                    right_current -= right_pq.top();
                    right_pq.pop();
                    right_pq.push(w);
                    right_current += w;
                }
            }
            if ((m - i) >= n) {
                right_sum[i] = right_current;
            }
        }

        long long min_total = LLONG_MAX;
        int best_k = -1;

        for (int k = n - 1; k <= m - n - 1; ++k) {
            if (left_sum[k] == LLONG_MAX || right_sum[k + 1] == LLONG_MAX) continue;
            long long total = left_sum[k] + right_sum[k + 1];
            if (total < min_total) {
                min_total = total;
                best_k = k;
            }
        }

        vector<Point> left_candidates(points.begin(), points.begin() + best_k + 1);
        sort(left_candidates.begin(), left_candidates.end());
        vector<Point> left_group(left_candidates.begin(), left_candidates.begin() + n);

        vector<Point> right_candidates(points.begin() + best_k + 1, points.end());
        sort(right_candidates.begin(), right_candidates.end());
        vector<Point> right_group(right_candidates.begin(), right_candidates.begin() + n);
        reverse(right_group.begin(), right_group.end());

        cout << min_total << '\n';
        for (int i = 0; i < n; ++i) {
            cout << left_group[i].idx << ' ' << right_group[i].idx << '\n';
        }
    }

    return 0;
}

