using namespace std;

int pow10_mod(int exponent, int mod) {
    // 10^exponent mod mod, using precomputed cycle since 10^6  1 mod7
    int cycle[6] = {1, 3, 2, 6, 4, 5};
    return cycle[exponent % 6];
}

bool can_form(int required, const string& others) {
    if (others.empty()) return required == 0;
    vector<int> cnt(10, 0);
    for (char c : others) cnt[c - '0']++;
    bool all_zero = true;
    for (int i = 1; i < 10; ++i)
        if (cnt[i] > 0) { all_zero = false; break; }
    if (all_zero) return false;
    // Check if any leading digit can work
    for (int d = 9; d >= 1; --d) {
        if (cnt[d] == 0) continue;
        int k = others.size();
        int pow_val = pow10_mod(k - 1, 7);
        int l_contrib = (d * pow_val) % 7;
        int rem_required = (required - l_contrib) % 7;
        if (rem_required < 0) rem_required += 7;
        vector<int> rem_cnt = cnt;
        rem_cnt[d]--;
        bool rem_all_zero = true;
        for (int i = 1; i < 10; ++i)
            if (rem_cnt[i] > 0) { rem_all_zero = false; break; }
        if (rem_all_zero) {
            if (rem_required == 0) return true;
        } else {
            return true;
        }
    }
    return false;
}

string build_x(int required, string others) {
    if (others.empty()) return "";
    sort(others.rbegin(), others.rend());
    int k = others.size();
    vector<int> cnt(10, 0);
    for (char c : others) cnt[c - '0']++;
    for (int d = 9; d >= 1; --d) {
        if (cnt[d] == 0) continue;
        cnt[d]--;
        string s(1, '0' + d);
        for (int i = 9; i >= 0; --i)
            s += string(cnt[i], '0' + i);
        int mod = 0;
        for (char c : s) mod = (mod * 10 + (c - '0')) % 7;
        if (mod == required) return s;
        cnt[d]++;
    }
    return "";
}

