using namespace std;

vector<int> find_reachable(vector<int> r, vector<int> u, vector<int> v, vector<int> c) {
    int n = r.size();
    int m = u.size();

    // Preprocess connectors into groups by their required key
    vector<vector<pair<int, int>>> key_connectors(n);
    for (int j = 0; j < m; ++j) {
        key_connectors[c[j]].emplace_back(u[j], v[j]);
    }

    vector<int> p(n, 0);

    for (int s = 0; s < n; ++s) {
        vector<bool> reachable(n, false);
        vector<bool> collected(n, false);
        queue<int> q;

        // Initial setup: start at room s, collect its key
        reachable[s] = true;
        int initial_key = r[s];
        if (!collected[initial_key]) {
            collected[initial_key] = true;
            q.push(initial_key);
        }

        while (!q.empty()) {
            int k = q.front();
            q.pop();

            for (auto &conn : key_connectors[k]) {
                int uj = conn.first;
                int vj = conn.second;

                // Check uj -> vj
                if (reachable[uj] && !reachable[vj]) {
                    reachable[vj] = true;
                    int new_key = r[vj];
                    if (!collected[new_key]) {
                        collected[new_key] = true;
                        q.push(new_key);
                    }
                }

                // Check vj -> uj
                if (reachable[vj] && !reachable[uj]) {
                    reachable[uj] = true;
                    int new_key = r[uj];
                    if (!collected[new_key]) {
                        collected[new_key] = true;
                        q.push(new_key);
                    }
                }
            }
        }

        // Count reachable rooms
        int count = 0;
        for (bool val : reachable) {
            if (val) count++;
        }
        p[s] = count;
    }

    // Find the minimum p[i]
    int min_p = n + 1;
    for (int val : p) {
        if (val < min_p) {
            min_p = val;
        }
    }

    vector<int> ans(n, 0);
    for (int i = 0; i < n; ++i) {
        if (p[i] == min_p) {
            ans[i] = 1;
        }
    }

    return ans;
}

