typedef long long ll;
const ll mod=1e9+7;

ll powmod(ll a, ll b, ll m) {
    ll res=1;
    while(b>0) {
        if(b&1) res=res*a%m;
        a=a*a%m;
        b>>=1;
    }
    return res;
}

