        cin>>a;
        cnt[a%x]++;
    }
    vector<vector<ll>> dp(2,vector<ll>(x));
    for(ll i=0;i<x;i++) {
        dp[0][i]=cnt[i]%mod;
    }
    ll pow10=10%x;
    for(ll i=1;i<b;i++) {
        fill(dp[i&1].begin(),dp[i&1].end(),0);
        for(ll j=0;j<x;j++) {
            if(!dp[(i-1)&1][j]) continue;
            for(ll l=0;l<x;l++) {
                ll rem=(j*pow10+l)%x;
                dp[i&1][rem]=(dp[i&1][rem]+dp[(i-1)&1][j]*cnt[l])%mod;
            }
        }
        pow10=pow10*10%x;
    }
    cout<<dp[(b-1)&1][k]<<'\n';
    return 0;
}

