static int K, N;
static bool closure[300][300]; // closure[i][j] means i can reach j (i is taller than j)

void init(int k, vector<int> r) {
    K = k;
    N = r.size();
    vector<vector<int>> adj(N);

    // Step 1: Build initial edges from r[i] = 0 and r[j] = K-1.
    for (int j = 0; j < N; ++j) {
        if (r[j] == K - 1) {
            for (int step = 1; step < K; ++step) {
                int i = (j + step) % N;
                adj[i].push_back(j);
            }
        }
    }
    for (int i = 0; i < N; ++i) {
        if (r[i] == 0) {
            for (int step = 1; step < K; ++step) {
                int j = (i + step) % N;
                adj[i].push_back(j);
            }
        }
    }

    // Step 2: Compute transitive closure using BFS for each node.
    memset(closure, 0, sizeof(closure));
    for (int i = 0; i < N; ++i) {
        queue<int> q;
        q.push(i);
        closure[i][i] = true;
        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (int v : adj[u]) {
                if (!closure[i][v]) {
                    closure[i][v] = true;
                    q.push(v);
                }
            }
        }
    }

    // Step 3: Iteratively propagate constraints based on transitive closure.
    bool changed;
    do {
        changed = false;
        for (int i = 0; i < N; ++i) {
            vector<int> window;
            for (int step = 1; step < K; ++step) {
                int j = (i + step) % N;
                window.push_back(j);
            }

            int count = 0;
            for (int j : window) {
                if (closure[j][i]) {
                    ++count;
                }
            }

            if (count == r[i]) {
                for (int j : window) {
                    if (!closure[j][i] && !closure[i][j]) {
                        adj[i].push_back(j);
                        // Update closure for all nodes that can reach i
                        for (int x = 0; x < N; ++x) {
                            if (closure[x][i] && !closure[x][j]) {
                                closure[x][j] = true;
                                changed = true;
                                // Propagate further
                                queue<int> q_update;
                                q_update.push(x);
                                while (!q_update.empty()) {
                                    int u = q_update.front();
                                    q_update.pop();
                                    for (int v : adj[u]) {
                                        if (!closure[x][v] && closure[x][u]) {
                                            closure[x][v] = true;
                                            q_update.push(v);
                                            changed = true;
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    } while (changed);
}

int compare_plants(int x, int y) {
    if (closure[x][y]) return 1;
    if (closure[y][x]) return -1;
    return 0;
}

