using namespace std;

struct Edge {
    int to;
    int weight;
};

vector<vector<Edge>> build_adj(int N, vector<int>& U, vector<int>& V, vector<int>& W) {
    vector<vector<Edge>> adj(N);
    for (int i = 0; i < N-1; ++i) {
        int u = U[i];
        int v = V[i];
        int w = W[i];
        adj[u].push_back({v, w});
        adj[v].push_back({u, w});
    }
    return adj;
}

vector<long long> compute_dist(int start, int N, vector<vector<Edge>>& adj) {
    vector<long long> dist(N, -1);
    queue<int> q;
    dist[start] = 0;
    q.push(start);
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (auto& e : adj[u]) {
            if (dist[e.to] == -1) {
                dist[e.to] = dist[u] + e.weight;
                q.push(e.to);
            }
        }
    }
    return dist;
}

vector<int> find_path(int X, int Y, vector<vector<Edge>>& adj) {
    int N = adj.size();
    vector<int> parent(N, -1);
    queue<int> q;
    q.push(X);
    parent[X] = -1;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        if (u == Y) break;
        for (auto& e : adj[u]) {
            if (parent[e.to] == -1) {
                parent[e.to] = u;
                q.push(e.to);
            }
        }
    }
    vector<int> path;
    int current = Y;
    while (current != -1) {
        path.push_back(current);
        current = parent[current];
    }
    reverse(path.begin(), path.end());
    return path;
}

int max_score(int N, int X, int Y, long long K,
              vector<int> U, vector<int> V, vector<int> W) {
    auto adj = build_adj(N, U, V, W);
    auto dx = compute_dist(X, N, adj);
    auto dy = compute_dist(Y, N, adj);
    vector<int> path = find_path(X, Y, adj);

    vector<bool> is_on_path(N, false);
    for (int u : path) {
        is_on_path[u] = true;
    }

    vector<pair<long long, int>> branches;

    if (path.size() > 1) {
        int next_X = path[1];
        for (auto& e : adj[X]) {
            int u = e.to;
            if (u == next_X || is_on_path[u]) continue;
            long long sum_dx = 0;
            int count = 0;
            queue<int> q;
            vector<bool> visited(N, false);
            q.push(u);
            visited[u] = true;
            sum_dx += dx[u];
            count++;
            while (!q.empty()) {
                int v = q.front();
                q.pop();
                for (auto& e2 : adj[v]) {
                    int w = e2.to;
                    if (w == X || is_on_path[w] || visited[w]) continue;
                    visited[w] = true;
                    sum_dx += dx[w];
                    count++;
                    q.push(w);
                }
            }
            if (sum_dx <= K) {
                branches.emplace_back(sum_dx, count);
            }
        }
    }

    if (path.size() >= 2) {
        int prev_Y = path[path.size()-2];
        for (auto& e : adj[Y]) {
            int u = e.to;
            if (u == prev_Y || is_on_path[u]) continue;
            long long sum_dy = 0;
            int count = 0;
            queue<int> q;
            vector<bool> visited(N, false);
            q.push(u);
            visited[u] = true;
            sum_dy += dy[u];
            count++;
            while (!q.empty()) {
                int v = q.front();
                q.pop();
                for (auto& e2 : adj[v]) {
                    int w = e2.to;
                    if (w == Y || is_on_path[w] || visited[w]) continue;
                    visited[w] = true;
                    sum_dy += dy[w];
                    count++;
                    q.push(w);
                }
            }
            if (sum_dy <= K) {
                branches.emplace_back(sum_dy, count);
            }
        }
    }

    sort(branches.begin(), branches.end(), [](const pair<long long, int>& a, const pair<long long, int>& b) {
        return (long long)a.second * b.first > (long long)b.second * a.first;
    });

    long long total_cost = 0;
    int total_value = 0;
    for (auto& branch : branches) {
        if (total_cost + branch.first <= K) {
            total_cost += branch.first;
            total_value += branch.second;
        }
    }

    return total_value + 2;
}

