using namespace std;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    unordered_map<int, int> even_masks, odd_masks;

    for (int i = 0; i < n; ++i) {
        string s;
        cin >> s;

        int mask = 0;
        for (char c : s) {
            mask ^= (1 << (c - 'a'));
        }

        int len_parity = s.size() % 2;

        if (len_parity % 2 == 0) {
            even_masks[mask]++;
        } else {
            odd_masks[mask]++;
        }
    }

    vector<int> targets;
    for (int i = 0; i < 26; ++i) {
        targets.push_back( ((1 << 26) - 1) ^ (1 << i) );
    }

    long long total = 0;

    for (int target : targets) {
        // even -> odd
        for (const auto& [x, cnt_x] : even_masks) {
            int y = x ^ target;
            auto it = odd_masks.find(y);
            if (it != odd_masks.end()) {
                total += (long long) cnt_x * it->second;
            }
        }

        // odd -> even
        for (const auto& [x, cnt_x] : odd_masks) {
            int y = x ^ target;
            auto it = even_masks.find(y);
            if (it != even_masks.end()) {
                total += (long long) cnt_x * even_masks[y];
            }
        }
    }

    cout << total << '\n';

    return 0;
}

