struct edge {
    int u, v, w, id;
    bool operator<(const edge& o) const {
        return w < o.w;
    }
};

vector<int> p;

int find(int x) {
    return p[x] == x ? x : p[x] = find(p[x]);
}

bool unite(int x, int y) {
    x = find(x);
    y = find(y);
    if (x == y) return false;
    p[y] = x;
    return true;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    int n, m;
    cin >> n >> m;
    vector<edge> edges(m);
    for (int i = 0; i < m; i++) {
        cin >> edges[i].u >> edges[i].v >> edges[i].w;
        edges[i].id = i;
    }
    sort(edges.begin(), edges.end());
    p.resize(n + 1);
    for (int i = 1; i <= n; i++) p[i] = i;
    ll res = 0;
    for (int i = 0; i < m; ) {
        int j = i;
        while (j < m && edges[j].w == edges[i].w) j++;
        int cnt = 0;
        for (int k = i; k < j; k++) {
            if (find(edges[k].u) != find(edges[k].v)) {
                cnt++;
            }
        }
        for (int k = i; k < j; k++) {
            if (unite(edges[k].u, edges[k].v)) {
                cnt--;
            }
        }
        res += cnt;
        i = j;
    }
    cout << res << '\n';
    return 0;
}

