#include <bits/stdc++.h>
using namespace std;

int main() {
    int n, m, k, p;
    cin >> n >> m >> k >> p;

    vector<vector<int>> C(n + 1, vector<int>(n + 1, 0));
    for (int i = 0; i <= n; ++i) {
        C[i][0] = 1;
        for (int j = 1; j <= i; ++j)
            C[i][j] = (C[i - 1][j - 1] + C[i - 1][j]) % p;
    }

    vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(m + 2, vector<int>(n + 1, 0)));

    for (int d = 1; d <= m; ++d)
        dp[0][d][0] = 1;

    for (int d = 1; d <= m; ++d) {
        int c = (d == m) ? 1 : 0;
        if (c <= n)
            dp[1][d][c] = 1;
    }

    for (int s = 2; s <= n; ++s) {
        for (int d = 1; d <= m; ++d) {
            for (int c = 0; c <= s; ++c) {
                for (int l = 0; l <= s - 1; ++l) {
                    int r = s - 1 - l;
                    int new_d = d + 1;

                    if (new_d > m) {
                        int ways_left = (l == 0) ? 1 : 0;
                        int ways_right = (r == 0) ? 1 : 0;
                        int delta = (d == m) ? 1 : 0;
                        if (0 + 0 + delta == c) {
                            int ways = (ways_left * ways_right) % p;
                            ways = (ways * C[s - 1][l]) % p;
                            dp[s][d][c] = (dp[s][d][c] + ways) % p;
                        }
                    } else {
                        for (int cl = 0; cl <= l; ++cl) {
                            for (int cr = 0; cr <= r; ++cr) {
                                int delta = (d == m) ? 1 : 0;
                                if (cl + cr + delta != c)
                                    continue;
                                int ways = (dp[l][new_d][cl] * 1LL * dp[r][new_d][cr]) % p;
                                ways = (ways * C[s - 1][l]) % p;
                                dp[s][d][c] = (dp[s][d][c] + ways) % p;
                            }
                        }
                    }
                }
            }
        }
    }

    cout << dp[n][1][k] << endl;
    return 0;
}

