using namespace std;

vector<vector<int>> adj;

void init(int N, int S, vector<int> T,
          vector<vector<int>> A, vector<vector<int>> B) {
    adj.clear();
    adj.resize(N);

    for (int u = 0; u < N; ++u) {
        for (int v = 0; v < N; ++v) {
            if (u == v) continue;
            bool found = false;
            for (int i = 0; i < T[u]; ++i) {
                int a1 = A[u][i];
                int b1 = B[u][i];
                for (int j = 0; j < T[v]; ++j) {
                    int a2 = A[v][j];
                    int b2 = B[v][j];
                    if (a1 <= b2 && a2 <= b1) {
                        found = true;
                        break;
                    }
                }
                if (found) break;
            }
            if (found) {
                adj[u].push_back(v);
            }
        }
    }
}

int count_users(int P) {
    int N = adj.size();
    vector<bool> visited(N, false);
    queue<int> q;
    q.push(P);
    visited[P] = true;
    int count = 1;

    while (!q.empty()) {
        int u = q.front();
        q.pop();

        for (int v : adj[u]) {
            if (!visited[v]) {
                visited[v] = true;
                count++;
                q.push(v);
            }
        }
    }

    return count;
}

