vector<bool> compute_prefix_zero(const string& s) {
    vector<bool> prefix(s.size() + 1, false);
    for (int i = 0; i < s.size(); ++i) {
        prefix[i+1] = prefix[i] || (s[i] == '0');
    }
    return prefix;
}

vector<bool> compute_suffix_zero(const string& s) {
    vector<bool> suffix(s.size() + 1, false);
    for (int i = s.size() - 1; i >= 0; --i) {
        suffix[i] = suffix[i+1] || (s[i] == '0');
    }
    return suffix;
}

bool is_sum_equal(const string& a, const string& b, const string& x) {
    int i = a.size() - 1;
    int j = b.size() - 1;
    int k = x.size() - 1;
    int carry = 0;
    while (i >= 0 || j >= 0 || carry > 0) {
        int digit_a = i >= 0 ? a[i--] - '0' : 0;
        int digit_b = j >= 0 ? b[j--] - '0' : 0;
        int sum = digit_a + digit_b + carry;
        carry = sum / 10;
        int current = sum % 10;
        if (k < 0) {
            return false;
        }
        if (current != x[k--] - '0') {
            return false;
        }
    }
    return k == -1;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    string s, x;
    cin >> s >> x;
    int n = s.size();
    int len_x = x.size();
    auto prefix_zero = compute_prefix_zero(s);
    auto suffix_zero = compute_suffix_zero(s);
    for (int k = 1; k < n; ++k) {
        if (prefix_zero[k] || suffix_zero[k]) continue;
        int len_a = k;
        int len_b = n - k;
        int max_len = max(len_a, len_b);
        if (max_len != len_x && max_len != len_x - 1) continue;
        string a = s.substr(0, k);
        string b = s.substr(k);
        if (is_sum_equal(a, b, x)) {
            cout << "1 " << k << "\n";
            cout << k+1 << " " << n << "\n";
            return 0;
        }
    }
    return 0;
}

