int main() {
  string s, m;
  cin >> s >> m;

  if (s.length() != m.length()) {
    cout << "R.I.P." << endl;
    return 0;
  }

  string key = "";
  for (int i = 0; i < s.length(); ++i) {
    char s_char = tolower(s[i]);
    char m_char = tolower(m[i]);

    if (!isalpha(s_char) || !isalpha(m_char)) {
      cout << "R.I.P." << endl;
      return 0;
    }

    int s_val = s_char - 'a';
    int m_val = m_char - 'a';

    int diff = (s_val - m_val + 26) % 26;
    key += to_string(diff);
  }

  bool possible = true;
  for (int i = 0; i < s.length(); ++i) {
    char m_char = tolower(m[i]);
    int m_val = m_char - 'a';
    int k = key[i] - '0';

    int decrypted_val = (m_val + k) % 26;
    char decrypted_char = (char)(decrypted_val + 'a');

    if (tolower(s[i]) != decrypted_char) {
      possible = false;
      break;
    }
  }

  if (possible) {
    cout << key << endl;
  } else {
    cout << "R.I.P." << endl;
  }

  return 0;
}

