typedef long long ll;

void solve() {
    int n;
    cin >> n;
    vector<int> a(n);
    for (int i = 0; i < n; i++) cin >> a[i];
    vector<ll> b(n);
    for (int i = 0; i < n; i++) cin >> b[i];

    vector<ll> fire, frost;
    for (int i = 0; i < n; i++) {
        if (a[i] == 0) fire.push_back(b[i]);
        else frost.push_back(b[i]);
    }

    int x = fire.size(), y = frost.size();
    ll sum_all = accumulate(fire.begin(), fire.end(), 0LL) + accumulate(frost.begin(), frost.end(), 0LL);

    if (x == 0 || y == 0) {
        cout << sum_all << '\n';
        return;
    }

    sort(fire.begin(), fire.end(), greater<>());
    sort(frost.begin(), frost.end(), greater<>());
    ll sum_trans = 0;

    if (x != y) {
        if (x > y) {
            ll sum_f = accumulate(fire.begin(), fire.begin() + y + 1, 0LL);
            sum_f -= fire[y];
            ll sum_fr = accumulate(frost.begin(), frost.begin() + y, 0LL);
            sum_trans = sum_f + sum_fr;
        } else {
            ll sum_fr = accumulate(frost.begin(), frost.begin() + x + 1, 0LL);
            sum_fr -= frost[x];
            ll sum_f = accumulate(fire.begin(), fire.begin() + x, 0LL);
            sum_trans = sum_fr + sum_f;
        }
    } else {
        ll sum_fire_all = accumulate(fire.begin(), fire.end(), 0LL);
        ll sum_frost_all = accumulate(frost.begin(), frost.end(), 0LL);
        ll option1 = sum_frost_all + sum_fire_all - fire.back();
        ll option2 = sum_fire_all + sum_frost_all - frost.back();
        sum_trans = max(option1, option2);
    }

    cout << sum_all + sum_trans << '\n';
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    int t;
    cin >> t;
    while (t--) solve();
    return 0;
}

