int main() {
    int n, k;
    cin >> n >> k;
    vector<int> x(n + 1);
    for (int i = 0; i <= n; ++i) {
        cin >> x[i];
    }
    int y_val;
    cin >> y_val;

    int L = *min_element(x.begin(), x.begin() + n);
    int R = *max_element(x.begin(), x.begin() + n);
    int xn = x.back();
    int yn = y_val;

    double ans;
    if (k <= n) {
        int start_x = x[k - 1];
        int start_y = 0;

        double optionA = abs(start_x - L) + (R - L) + hypot(xn - R, yn);
        double optionB = abs(start_x - R) + (R - L) + hypot(xn - L, yn);
        double optionC = hypot(start_x - xn, start_y - yn) + hypot(L - xn, yn) + (R - L);
        double optionD = hypot(start_x - xn, start_y - yn) + hypot(R - xn, yn) + (R - L);
        double optionE = abs(start_x - L) + hypot(L - xn, yn) + hypot(R - xn, yn);
        double optionF = abs(start_x - R) + hypot(R - xn, yn) + hypot(L - xn, yn);

        ans = min({optionA, optionB, optionC, optionD, optionE, optionF});
    } else {
        double optionG = hypot(L - xn, yn) + (R - L);
        double optionH = hypot(R - xn, yn) + (R - L);
        ans = min(optionG, optionH);
    }

    cout << fixed << setprecision(15) << ans << endl;

    return 0;
}

