bool check(int start_bit, const unordered_map<char, vector<int>>& char_positions) {
    for (const auto& entry : char_positions) {
        const vector<int>& positions = entry.second;
        int first = positions[0];
        int required = (start_bit + first) % 2;
        for (size_t i = 1; i < positions.size(); ++i) {
            int idx = positions[i];
            if ((start_bit + idx) % 2 != required) {
                return false;
            }
        }
    }
    return true;
}

