using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) {
        int n, p;
        cin >> n >> p;
        vector<int> a(n), b(n);
        for (auto &x : a) cin >> x;
        for (auto &x : b) cin >> x;

        vector<pair<int, int>> residents;
        for (int i = 0; i < n; ++i) {
            residents.emplace_back(b[i], a[i]);
        }

        vector<pair<int, int>> S;
        for (auto &res : residents) {
            if (res.first < p) {
                S.push_back(res);
            }
        }
        if (S.empty()) {
            cout << 1LL * n * p << '\n';
            continue;
        }
        sort(S.begin(), S.end());
        auto initial = S[0];

        vector<pair<int, int>> remaining;
        bool removed = false;
        for (auto &res : residents) {
            if (!removed && res == initial) {
                removed = true;
                continue;
            }
            remaining.push_back(res);
        }
        sort(remaining.begin(), remaining.end());

        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<>> pq;
        pq.push(initial);

        long long cost = p;
        for (auto &res : remaining) {
            if (pq.empty()) {
                cost += p;
                continue;
            }
            auto [curr_b, curr_a] = pq.top();
            pq.pop();
            cost += curr_b;
            if (--curr_a > 0) {
                pq.emplace(curr_b, curr_a);
            }
            if (res.first < p) {
                pq.push(res);
            }
        }

        cout << cost << '\n';
    }
    return 0;
}

