    for (auto& x : b) cin >> x;
    for (auto& x : c) cin >> x;

    ll sum_b = accumulate(b.begin(), b.end(), 0LL);
    ll sum_c = accumulate(c.begin(), c.end(), 0LL);

    ll total = sum_b + sum_c;
    if (total % (2 * n) != 0) {
        cout << "-1\n";
        return 0;
    }

    ll sum_a = total / (2 * n);
    vector<ll> a(n);
    for (int i = 0; i < n; ++i) {
        ll bc = b[i] + c[i];
        ll temp = bc - sum_a;
        if (temp < 0 || temp % n != 0) {
            cout << "-1\n";
            return 0;
        }
        a[i] = temp / n;
        if (a[i] < 0) {
            cout << "-1\n";
            return 0;
        }
    }

    vector<int> s(31, 0);
    for (auto x : a) {
        for (int k = 0; k < 31; ++k) {
            if (x & (1LL << k)) {
                s[k]++;
            }
        }
    }

    for (int i = 0; i < n; ++i) {
        ll exp_b = 0, exp_c = 0;
        for (int k = 0; k < 31; ++k) {
