    int right = -1;
    int ways1 = 0;
    int ways0 = 0;
    bool is_source = false;
};

vector<Node> nodes;
int N, M;

void update(int idx) {
    if (nodes[idx].is_source) return;
    int left = nodes[idx].left;
    int right = nodes[idx].right;
    long long left1 = nodes[left].ways1;
    long long left0 = nodes[left].ways0;
    long long right1 = nodes[right].ways1;
    long long right0 = nodes[right].ways0;

    long long ways1 = (left0 * right1 % MOD + left1 * right0 % MOD) % MOD;
    ways1 = ways1 * 1 % MOD;
    long long ways1_2 = (left1 * right1 % MOD) * 2 % MOD;
    ways1 = (ways1 + ways1_2) % MOD;

    long long ways0 = (left0 * right0 % MOD) * 2 % MOD;
    long long ways0_1 = (left0 * right1 % MOD + left1 * right0 % MOD) % MOD;
    ways0_1 = ways0_1 * 1 % MOD;
    ways0 = (ways0 + ways0_1) % MOD;

    nodes[idx].ways1 = (int)ways1;
    nodes[idx].ways0 = (int)ways0;
}

void init(int N_, int M_, vector<int> P, vector<int> A) {
    N = N_;
    M = M_;
    int total = N + M;
    nodes.resize(total);

    for (int i = 1; i < total; ++i) {
        int parent = P[i];
        if (nodes[parent].left == -1) {
            nodes[parent].left = i;
        } else {
            nodes[parent].right = i;
        }
    }

    for (int i = N; i < total; ++i) {
        nodes[i].is_source = true;
        nodes[i].ways1 = A[i - N];
        nodes[i].ways0 = 1 - A[i - N];
    }

    for (int i = N - 1; i >= 0; --i) {
        nodes[i].is_source = false;
        update(i);
    }
}

int count_ways(int L, int R) {
    for (int i = L; i <= R; ++i) {
        swap(nodes[i].ways1, nodes[i].ways0);
        int current = i;
        while (true) {
            int parent = (current - 1) / 2;
            if (parent < 0) break;
            update(parent);
            current = parent;
        }
    }
    return nodes[0].ways1 % MOD;
}

#include <bits/stdc++.h>
using namespace std;

void solve() {
    int n;
    cin >> n;

    vector<vector<int>> sets(n);
    vector<bool> is_big(n);
    const int threshold = 500;

    for (int i = 0; i < n; ++i) {
        int k;
        cin >> k;
        vector<int> elements(k);
        for (int j = 0; j < k; ++j) {
            cin >> elements[j];
        }
        sets[i] = elements;
        is_big[i] = (k >= threshold);
    }

    // Process small sets
    unordered_map<long long, int> pair_map;
    bool found = false;

    for (int i = 0; i < n; ++i) {
        if (is_big[i]) continue;

        auto& s = sets[i];
        sort(s.begin(), s.end());
        int sz = s.size();

        for (int a = 0; a < sz; ++a) {
            for (int b = a + 1; b < sz; ++b) {
                int x = s[a], y = s[b];
                if (x > y) swap(x, y);
                long long key = (1LL * x) << 32 | y;
                auto it = pair_map.find(key);
                if (it != pair_map.end()) {
                    cout << it->second + 1 << " " << i + 1 << "\n";
                    found = true;
                    goto end;
                }
            }
        }

        for (int a = 0; a < sz; ++a) {
            for (int b = a + 1; b < sz; ++b) {
                int x = s[a], y = s[b];
                if (x > y) swap(x, y);
                long long key = (1LL * x) << 32 | y;
                if (pair_map.find(key) == pair_map.end()) {
                    pair_map[key] = i;
                }
            }
        }
    }

    if (found) goto end;

    // Process big sets
    for (int i = 0; i < n; ++i) {
        if (!is_big[i]) continue;

        auto& elements = sets[i];
        unordered_set<int> big_set(elements.begin(), elements.end());

        for (int j = 0; j < n; ++j) {
            if (i == j) continue;

            int cnt = 0;
            for (int x : sets[j]) {
                if (big_set.count(x)) {
                    if (++cnt >= 2) {
                        cout << i + 1 << " " << j + 1 << "\n";
                        found = true;
                        goto end;
                    }
                }
            }
        }
    }

end:
    if (!found) cout << "-1\n";
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) solve();

    return 0;
}

