struct Treasure {
    long long ai;
    int ci;
    bool taken;
};

vector<Treasure> treasures;
unordered_map<long long, int> ai_to_index;
multiset<pair<long long, long long>> ms; // (-ci, ai)
unordered_set<long long> in_ms;
long long current_d;

long long gcd(long long a, long long b) {
    while (b) {
        a %= b;
        swap(a, b);
    }
    return a;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int h, n, m, k;
    cin >> h >> n >> m >> k;

    treasures.reserve(n);
    ai_to_index.reserve(n);

    for (int i = 0; i < n; ++i) {
        long long ai;
        int ci;
        cin >> ai >> ci;
        treasures.push_back({ai, ci, false});
        ai_to_index[ai] = i;
    }

    current_d = k;

    for (int i = 0; i < n; ++i) {
        auto& t = treasures[i];
        if ((t.ai - 1) % current_d == 0) {
            ms.insert({ -t.ci, t.ai });
            in_ms.insert(t.ai);
        }
    }

    while (m--) {
        int type;
        cin >> type;
        if (type == 1) {
            long long x;
            cin >> x;
            current_d = gcd(current_d, x);
            for (int i = 0; i < n; ++i) {
                auto& t = treasures[i];
                if (t.taken) continue;
                if ((t.ai - 1) % current_d != 0) continue;
                if (in_ms.count(t.ai)) continue;
                ms.insert({ -t.ci, t.ai });
                in_ms.insert(t.ai);
            }
        } else if (type == 2) {
            int x, y;
            cin >> x >> y;
            x--;
            auto& t = treasures[x];
            if (t.taken) continue;
            int old_ci = t.ci;
            t.ci -= y;
            if (in_ms.count(t.ai)) {
                auto it = ms.find({ -old_ci, t.ai });
                if (it != ms.end()) {
                    ms.erase(it);
                    in_ms.erase(t.ai);
                }
                if ((t.ai - 1) % current_d == 0) {
                    ms.insert({ -t.ci, t.ai });
                    in_ms.insert(t.ai);
                }
            }
        } else if (type == 3) {
            if (ms.empty()) {
                cout << "0\n";
                continue;
            }
            auto it = ms.begin();
            long long ci = -it->first;
            long long ai = it->second;
            ms.erase(it);
            in_ms.erase(ai);
            cout << ci << '\n';
            int idx = ai_to_index[ai];
            treasures[idx].taken = true;
        }
    }

    return 0;
}

