using namespace std;

vector<int> X, Y, W;
int N;
long long max_sum;

void compute(const vector<int>& piers) {
    long long sum = 0;
    for (int i = 0; i < X.size(); ++i) {
        int c = X[i], r = Y[i];
        int k_own = piers[c];
        if (k_own > r) {
            continue;
        }
        bool left_ok = false;
        if (c > 0) {
            int k_left = piers[c-1];
            if (k_left >= r + 1) {
                left_ok = true;
            }
        }
        bool right_ok = false;
        if (c < N - 1) {
            int k_right = piers[c+1];
            if (k_right >= r + 1) {
                right_ok = true;
            }
        }
        if (left_ok || right_ok) {
            sum += W[i];
        }
    }
    if (sum > max_sum) {
        max_sum = sum;
    }
}

void generate(int col, vector<int>& piers) {
    if (col == N) {
        compute(piers);
        return;
    }
    for (int k = 0; k <= N; ++k) {
        piers.push_back(k);
        generate(col + 1, piers);
        piers.pop_back();
    }
}

long long max_weights(int N_, int M, vector<int> X_, vector<int> Y_, vector<int> W_) {
    N = N_;
    X = X_;
    Y = Y_;
    W = W_;
    max_sum = 0;
    vector<int> piers;
    generate(0, piers);
    return max_sum;
}

