    vector<long long> d(n);
    for (auto &x : d) cin >> x;

    vector<long long> s(n);
    s[0] = d[0];
    for (int i = 1; i < n; ++i) {
        s[i] = s[i-1] + d[i];
    }

    for (int i = 0; i < n; ++i) {
        if (H + s[i] <= 0) {
            cout << i+1 << '\n';
