    long long min_time = LLONG_MAX;

    for (int k = 0; k < n; ++k) {
        long long required = H + s[k];
        long long m = (required - 1) / S;
        if (m < 0) m = 0;
        long long time_candidate = (m + 1) * n + (k + 1);
        min_time = min(min_time, time_candidate);
    }

    cout << min_time << '\n';

    return 0;
}

