int find_maximum_unique(int x, int y, std::vector<int> a, std::vector<int> b) {
    int count_free = 0;
    std::vector<int> paid_a;
    int n = a.size();
    for (int i = 0; i < n; ++i) {
        if (b[i] == 0) {
            count_free++;
        } else {
            paid_a.push_back(a[i]);
        }
    }
    std::sort(paid_a.begin(), paid_a.end());
    int sum = 0;
    int max_paid = 0;
    for (int i = 0; i < paid_a.size(); ++i) {
        sum += paid_a[i];
        if (sum <= x) {
            max_paid = i + 1;
        } else {
            break;
        }
    }
    return count_free + max_paid;
}

