typedef long long ll;

int main() {
  int n, m, p;
  cin >> n >> m >> p;

  vector<tuple<int, int, int>> edges;
  for (int i = 0; i < m; i++) {
    int u, v, w;
    cin >> u >> v >> w;
    edges.emplace_back(u, v, w);
  }

  vector<pair<int, int>> pairs;
  for (int i = 0; i < p; i++) {
    int a, b;
    cin >> a >> b;
    pairs.emplace_back(a, b);
  }

  int low = -1, high = 1000001;
  int ans = -1;

  while (low <= high) {
    int mid = low + (high - low) / 2;
    bool possible = true;

    for (auto [a, b] : pairs) {
      vector<vector<pair<int, int>>> adj(n + 1);
      for (auto [u, v, w] : edges) {
        if (w <= mid) continue;
        adj[u].emplace_back(v, w);
        adj[v].emplace_back(u, w);
      }

      vector<bool> visited(n + 1, false);
      queue<int> q;
      q.emplace(a);
      visited[a] = true;

      while (!q.empty()) {
        int u = q.front();
        q.pop();

        if (u == b) {
          possible = false;
          break;
        }

        for (auto [v, w] : adj[u]) {
          if (!visited[v]) {
            visited[v] = true;
            q.emplace(v);
          }
        }
      }

      if (!possible) break;
    }

    if (possible) {
      ans = mid;
      low = mid + 1;
    } else {
      high = mid - 1;
    }
  }

  cout << ans << '\n';

  return 0;
}

