#include <bits/stdc++.h>
using namespace std;

string build_palindrome(int freq[]) {
    string res;
    for (char c = 'a'; c <= 'z'; ++c) {
        while (freq[c] >= 2) {
            res.push_back(c);
            freq[c] -= 2;
        }
    }
    string middle;
    for (char c = 'a'; c <= 'z'; ++c) {
        if (freq[c] == 1) {
            middle = string(1, c);
            break;
        }
    }
    string right = res;
    reverse(right.begin(), right.end());
    return res + middle + right;
}

string build_non_palindrome(int freq[], int n) {
    string res(n, ' ');
    int left = 0, right = n-1;
    while (left <= right) {
        bool found = false;
        for (char c = 'a'; c <= 'z'; ++c) {
            if (freq[c] == 0) continue;
            freq[c]--;
            char d = 'a';
            for (; d <= c; ++d) {
                if (freq[d] > 0) break;
            }
            if (d <= c) {
                freq[d]--;
                res[left] = c;
                res[right] = d;
                left++;
                right--;
                found = true;
                break;
            } else {
                freq[c]++;
            }
        }
        if (!found) break;
    }
    if (left <= right) {
        for (char c = 'a'; c <= 'z'; ++c) {
            if (freq[c] > 0) {
                res[left] = c;
                break;
            }
        }
    }
    return res;
}

string solve(string s) {
    int freq[26] = {0};
    for (char c : s) freq[c-'a']++;

    int odd = 0;
    for (int i = 0; i < 26; ++i)
        if (freq[i] % 2) odd++;

    int n = s.size();
    if (odd <= 1) {
        int tmp[26];
        memcpy(tmp, freq, sizeof(tmp));
        return build_palindrome(tmp);
    } else {
        int tmp[26];
        memcpy(tmp, freq, sizeof(tmp));
        return build_non_palindrome(tmp, n);
    }
}

