#include <bits/stdc++.h>
using namespace std;

const int MAXN = 1e5 + 5;
const int LOG = 20;

int st_min[LOG][MAXN];
int st_max[LOG][MAXN];
int log_table[MAXN];

void build_sparse_tables(const vector<int>& a, int n) {
    for (int i = 0; i < n; ++i) {
        st_min[0][i] = a[i];
        st_max[0][i] = a[i];
    }

    for (int k = 1; k < LOG; ++k) {
        for (int i = 0; i + (1 << k) <= n; ++i) {
            st_min[k][i] = min(st_min[k-1][i], st_min[k-1][i + (1 << (k-1))]);
            st_max[k][i] = max(st_max[k-1][i], st_max[k-1][i + (1 << (k-1))]);
        }
    }

    log_table[1] = 0;
    for (int i = 2; i < MAXN; ++i) {
        log_table[i] = log_table[i/2] + 1;
    }
}

int get_min(int l, int r) {
    int len = r - l + 1;
    int k = log_table[len];
    return min(st_min[k][l], st_min[k][r - (1 << k) + 1]);
}

int get_max(int l, int r) {
    int len = r - l + 1;
    int k = log_table[len];
    return max(st_max[k][l], st_max[k][r - (1 << k) + 1]);
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, q;
    cin >> n >> q;

    vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    build_sparse_tables(a, n);

    while (q--) {
        int l, r;
        cin >> l >> r;

        if (l == 1 && r == n) {
            cout << 0 << '\n';
            continue;
        }

        int current_l = l, current_r = r;
        bool found = false;
        unordered_set<long long> visited;
        visited.insert((long long)current_l * (n + 1) + current_r);

        for (int step = 1; step <= 40; ++step) {
            int new_l = get_min(current_l - 1, current_r - 1);
            int new_r = get_max(current_l - 1, current_r - 1);

            if (new_l == 1 && new_r == n) {
                cout << step << '\n';
                found = true;
                break;
            }

            long long key = (long long)new_l * (n + 1) + new_r;
            if (visited.count(key)) {
                cout << -1 << '\n';
                found = true;
                break;
            }

            visited.insert(key);
            current_l = new_l;
            current_r = new_r;
        }

        if (!found) {
            cout << -1 << '\n';
        }
    }

    return 0;
}

