int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    vector<string> hashtags(n);
    for (int i = 0; i < n; ++i) {
        cin >> hashtags[i];
    }

    vector<string> processed(n);
    processed[n-1] = hashtags[n-1];
    for (int i = n-2; i >= 0; --i) {
        const string& current = hashtags[i];
        const string& next_str = processed[i+1];

        if (current <= next_str) {
            processed[i] = current;
            continue;
        }

        int len_cur = current.size();
        int len_next = next_str.size();
        int min_len = min(len_cur, len_next);
        int pos = 0;
        while (pos < min_len && current[pos] == next_str[pos]) {
            ++pos;
        }

        int m;
        if (pos < min_len) {
            m = pos - 1;
        } else {
            if (len_cur > len_next) {
                m = len_next - 1;
            } else {
                m = 0;
            }
        }

        m = min(m, len_cur - 1);
        m = max(m, 0);
        processed[i] = current.substr(0, m + 1);
    }

    for (const string& s : processed) {
        cout << s << '\n';
    }

    return 0;
}

