                long long d = x / current_m;
                long long max_m = x / d;
                max_m = min(max_m, upper_m);

                sum2 += d * (max_m - current_m + 1);
                current_m = max_m + 1;
            }
        }

        cout << sum1 + sum2 << '\n';
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

using ll = long long;

pair<ll, ll> compute_T_S(ll y, const vector<ll>& bits) {
    ll T = 0, S = 0;
    for (ll b : bits) {
        if (b > y) {
            ll d = b - y;
            if (d >= 60) return {LLONG_MAX, 0};
            ll cnt = 1LL << d;
            if (cnt > 1e18) return {LLONG_MAX, 0};
            T += cnt;
            S += cnt;
            if (T > 1e18) return {LLONG_MAX, 0};
        } else {
            T += 1;
        }
    }
    return {T, S};
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    ll n, k;
    cin >> n >> k;

    vector<ll> bits;
    ll temp = n;
    for (int i = 0; i <= 60; ++i) {
        if (temp & (1LL << i)) bits.push_back(i);
    }

    if (k < (ll)bits.size()) {
        cout << "No\n";
        return 0;
    }

    if (bits.empty()) {
        if (k == 0) {
            cout << "Yes\n";
        } else {
            cout << "No\n";
        }
        return 0;
    }

    ll highest_bit = bits.back();
    ll low = -1e18, high = highest_bit;
    ll ans_y = -1e18;

    while (low <= high) {
        ll mid = (low + high) / 2;
        auto [T_val, S_val] = compute_T_S(mid, bits);
        if (T_val == LLONG_MAX || T_val > k) {
            low = mid + 1;
        } else {
            ll max_possible = T_val + S_val - 1;
            if (max_possible >= k) {
                ans_y = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
    }

    if (ans_y == -1e18) {
        cout << "No\n";
        return 0;
    }

    vector<ll> y_terms;
    vector<ll> other_terms;
    for (ll b : bits) {
        if (b > ans_y) {
            ll d = b - ans_y;
            ll cnt = 1LL << d;
            for (ll i = 0; i < cnt; ++i) {
                y_terms.push_back(ans_y);
            }
        } else {
            other_terms.push_back(b);
        }
    }

    sort(other_terms.rbegin(), other_terms.rend());

    auto [T_val, S_val] = compute_T_S(ans_y, bits);
    ll s = k - T_val;

    vector<ll> new_y;
    ll remaining = S_val - s;
    new_y.reserve(remaining);
    for (ll i = 0; i < remaining; ++i) {
        new_y.push_back(ans_y);
    }

    vector<ll> split_terms;
    split_terms.reserve(2 * s);
    for (ll i = 0; i < 2 * s; ++i) {
        split_terms.push_back(ans_y - 1);
    }

    vector<ll> ans;
    ans.insert(ans.end(), new_y.begin(), new_y.end());
    ans.insert(ans.end(), other_terms.begin(), other_terms.end());
    ans.insert(ans.end(), split_terms.begin(), split_terms.end());

    cout << "Yes\n";
    for (size_t i = 0; i < ans.size(); ++i) {
        cout << ans[i] << " \n"[i == ans.size() - 1];
    }

    return 0;
}

