using namespace std;

struct DSU {
    vector<int> parent;
    DSU(int n) : parent(n) {
        for (int i = 0; i < n; ++i)
            parent[i] = i;
    }

    int find(int u) {
        if (parent[u] != u)
            parent[u] = find(parent[u]);
        return parent[u];
    }

    void unite(int u, int v) {
        u = find(u);
        v = find(v);
        if (u != v)
            parent[u] = v;
    }
};

int construct(vector<vector<int>> p) {
    int n = p.size();
    vector<vector<int>> answer(n, vector<int>(n, 0));

    // Validate initial conditions
    for (int i = 0; i < n; ++i) {
        if (p[i][i] != 1)
            return 0;
        for (int j = 0; j < n; ++j) {
            if (p[i][j] < 0 || p[i][j] > 3)
                return 0;
            if (p[i][j] != p[j][i])
                return 0;
        }
    }

    DSU dsu(n);
    for (int i = 0; i < n; ++i)
        for (int j = 0; j < n; ++j)
            if (p[i][j] >= 1)
                dsu.unite(i, j);

    // Check if components are correctly formed
    for (int i = 0; i < n; ++i)
        for (int j = 0; j < n; ++j)
            if ((p[i][j] >= 1) != (dsu.find(i) == dsu.find(j)))
                return 0;

    // Group into components
    vector<int> component(n, -1);
    int comp_id = 0;
    for (int i = 0; i < n; ++i) {
        if (component[i] == -1) {
            for (int j = 0; j < n; ++j)
                if (dsu.find(j) == dsu.find(i))
                    component[j] = comp_id;
            comp_id++;
        }
    }

    vector<bool> processed(comp_id, false);
    for (int i = 0; i < n; ++i) {
        int c = component[i];
        if (processed[c])
            continue;
        processed[c] = true;

        vector<int> nodes;
        for (int j = 0; j < n; ++j)
            if (component[j] == c)
                nodes.push_back(j);
        int m = nodes.size();

        bool all_ones = true;
        for (int u : nodes)
            for (int v : nodes)
                if (p[u][v] != 1) {
                    all_ones = false;
                    goto end_check;
                }
        end_check:

        if (all_ones) {
            // Build a star
            int root = nodes[0];
            for (int j = 1; j < m; ++j) {
                answer[root][nodes[j]] = 1;
                answer[nodes[j]][root] = 1;
            }
        } else {
            bool found_r = false;
            for (int candidate_r : nodes) {
                vector<int> S1, S2;
                for (int j : nodes) {
                    if (p[candidate_r][j] == 1)
                        S1.push_back(j);
                    else if (p[candidate_r][j] == 2)
                        S2.push_back(j);
                }

                if (S2.size() < 2)
                    continue;

                bool valid = true;
                for (int u : S2)
                    for (int v : S2)
                        if (p[u][v] != 2) {
                            valid = false;
                            break;
                        }
                if (!valid)
                    continue;

                for (int u : S1)
                    for (int v : S1)
                        if (p[u][v] != 1) {
                            valid = false;
                            break;
                        }
                if (!valid)
                    continue;

                for (int u : S1)
                    for (int v : S2)
                        if (p[u][v] != 2) {
                            valid = false;
                            break;
                        }
                if (!valid)
                    continue;

                // Valid r found
                found_r = true;
                // Connect S1 to r
                for (int u : S1)
                    if (u != candidate_r) {
                        answer[candidate_r][u] = 1;
                        answer[u][candidate_r] = 1;
                    }

                // Connect S2 in a cycle and to r
                int s = S2.size();
                for (int k = 0; k < s; ++k) {
                    int u = S2[k];
                    int v = S2[(k+1) % s];
                    answer[u][v] = 1;
                    answer[v][u] = 1;
                }
                for (int u : S2) {
                    answer[candidate_r][u] = 1;
                    answer[u][candidate_r] = 1;
                }
                break;
            }
            if (!found_r)
                return 0;
        }
    }

    build(answer);
    return 1;
}

