using namespace std;

vector<int> a;
vector<vector<int>> adj;
vector<int> gcd_subtree;

int compute_gcd(int u, int parent) {
    int g = a[u];
    for (int v : adj[u]) {
        if (v != parent) {
            g = gcd(g, compute_gcd(v, u));
        }
    }
    gcd_subtree[u] = g;
    return g;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) {
        int n, k;
        cin >> n >> k;
        a.resize(n);
        for (int i = 0; i < n; ++i) {
            cin >> a[i];
        }
        adj.assign(n, vector<int>());
        for (int i = 0; i < n - 1; ++i) {
            int u, v;
            cin >> u >> v;
            u--; v--;
            adj[u].push_back(v);
            adj[v].push_back(u);
        }

        gcd_subtree.resize(n);
        compute_gcd(0, -1);

        int root_gcd = gcd_subtree[0];
        if (k == 0) {
            cout << a[0] << '\n';
            continue;
        }

        vector<int> candidates;
        for (int u = 1; u < n; ++u) {
            candidates.push_back(gcd_subtree[u]);
        }

        sort(candidates.rbegin(), candidates.rend());
        priority_queue<int> pq;
        for (int g : candidates) {
            if (g == 0) continue;
            pq.push(g);
        }

        long long product = root_gcd;
        int operations = k - 1;
        while (!pq.empty() && operations > 0) {
            int g = pq.top();
            pq.pop();
            product *= g;
            operations--;
        }

        cout << a[0] * product << '\n';
    }

    return 0;
}

