int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    vector<pair<int, int>> constraints(n);
    for (int i = 0; i < n; ++i) {
        cin >> constraints[i].first >> constraints[i].second;
    }

    vector<int> perm1, perm2;
    set<int> available1, available2;

    for (int i = 1; i <= n; ++i) {
        available1.insert(i);
        available2.insert(i);
    }

    // Compute perm1 (greedy min)
    for (int i = 0; i < n; ++i) {
        int a = constraints[i].first;
        int b = constraints[i].second;
        auto it = available1.lower_bound(a);
        perm1.push_back(*it);
        available1.erase(it);
    }

    // Compute perm2 (greedy max)
    for (int i = 0; i < n; ++i) {
        int a = constraints[i].first;
        int b = constraints[i].second;
        auto it = available2.upper_bound(b);
        if (it != available2.begin()) --it;
        // Ensure the selected number is within [a, b]
        if (*it < a) {
            it = available2.lower_bound(a);
        }
        perm2.push_back(*it);
        available2.erase(it);
    }

    if (perm1 == perm2) {
        cout << "YES\n";
        for (int x : perm1) cout << x << ' ';
        cout << '\n';
    } else {
        cout << "NO\n";
        for (int x : perm1) cout << x << ' ';
        cout << '\n';
        for (int x : perm2) cout << x << ' ';
        cout << '\n';
    }

    return 0;
}

