int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, k;
    ll b;
    cin >> n >> k >> b;

    vector<pair<ll, int>> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i].first;
        a[i].second = i + 1; // original index is 1-based
    }

    // Sort in descending order of a_i
    sort(a.begin(), a.end(), [](const pair<ll, int>& x, const pair<ll, int>& y) {
        if (x.first != y.first) return x.first > y.first;
        return x.second < y.second;
    });

    // Precompute sum of top k and top k-1 elements
    ll sum_k = 0, sum_k_minus_1 = 0;
    for (int i = 0; i < k; ++i) sum_k += a[i].first;
    sum_k_minus_1 = sum_k - a[k-1].first;

    set<int> blocked_indices;
    for (int i = 0; i < k; ++i) blocked_indices.insert(a[i].second);

    ll threshold = a[k-1].first;

    int min_possible = n + 1;
    if (sum_k > b) {
        // Find the smallest s in the top k elements
        for (int i = 0; i < k; ++i) {
            if (a[i].second < min_possible) {
                min_possible = a[i].second;
            }
        }
    } else {
        // Check all s not in the top k elements
        for (int s = 1; s <= n; ++s) {
            bool is_blocked = blocked_indices.count(s);
            if (!is_blocked) {
                if (sum_k_minus_1 + a[s-1].first > b) {
                    if (s < min_possible) {
                        min_possible = s;
                    }
                }
            }
        }
    }

    if (min_possible <= n) {
        cout << min_possible << "\n";
    } else {
        // Find the worst possible square (max index not in blocked)
        int worst = 0;
        for (int s = 1; s <= n; ++s) {
            if (!blocked_indices.count(s) && s > worst) {
                worst = s;
            }
        }
        cout << worst << "\n";
    }

    return 0;
}

