vector<long long> prefix;
vector<int> W_current;
long long total_sum = 0;

void init(int N, vector<int> U, vector<int> V, vector<int> T, vector<int> W) {
    prefix.resize(N);
    prefix[0] = 0;
    for (int i = 1; i < N; ++i) {
        prefix[i] = prefix[i-1] + 2 * T[i-1];
    }
    W_current = W;
    total_sum = 0;
    for (int i = 0; i < N; ++i) {
        total_sum += static_cast<long long>(W_current[i]) * prefix[i];
    }
}

long long max_time(int S, int X) {
    int delta = X - W_current[S];
    total_sum += delta * prefix[S];
    W_current[S] = X;
    return total_sum;
}

