#include <bits/stdc++.h>
using namespace std;

vector<int> lexBFS(int n, const vector<vector<int>>& adj) {
    vector<set<int>> layers(n);
    vector<int> label(n, 0);
    vector<bool> inQueue(n, true);
    vector<int> order;

    for (int i = 0; i < n; ++i)
        layers[0].insert(i);

    int currentLayer = 0;
    for (int step = 0; step < n; ++step) {
        while (currentLayer >= 0 && layers[currentLayer].empty())
            --currentLayer;
        if (currentLayer < 0) break;

        int u = *layers[currentLayer].begin();
        layers[currentLayer].erase(u);
        order.push_back(u);
        inQueue[u] = false;

        vector<pair<int, int>> toMove;
        for (int v : adj[u]) {
            if (inQueue[v]) {
                int old = label[v];
                toMove.emplace_back(v, old);
                layers[old].erase(v);
                label[v]++;
                layers[old + 1].insert(v);
            }
        }

        for (auto [v, old] : toMove) {
            if (layers[old].empty() && old > currentLayer)
                currentLayer = old;
        }
    }

    return order;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;

    vector<vector<int>> adj(n);
    for (int i = 0; i < m; ++i) {
        int u, v;
        cin >> u >> v;
        --u; --v;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    for (int i = 0; i < n; ++i)
        sort(adj[i].begin(), adj[i].end());

    vector<int> order = lexBFS(n, adj);
    vector<int> pos(n);
    for (int i = 0; i < n; ++i)
        pos[order[i]] = i;

    bool possible = true;
    for (int u = 0; u < n; ++u) {
        int minPos = n, maxPos = -1;
        for (int v : adj[u]) {
            minPos = min(minPos, pos[v]);
            maxPos = max(maxPos, pos[v]);
        }
        if (adj[u].empty()) continue;
        int cnt = maxPos - minPos + 1;
        if (cnt != (int)adj[u].size())
            possible = false;
    }

    if (!possible) {
        cout << "NO\n";
        return 0;
    }

    vector<int> labels(n);
    for (int i = 0; i < n; ++i)
        labels[order[i]] = i;

    vector<unordered_set<int>> adjSet(n);
    for (int u = 0; u < n; ++u)
        for (int v : adj[u])
            adjSet[u].insert(v);

    for (int u = 0; u < n; ++u) {
        for (int v : adj[u]) {
            if (abs(labels[u] - labels[v]) > 1) {
                possible = false;
                break;
            }
        }
        if (!possible) break;
    }

    if (!possible) {
        cout << "NO\n";
        return 0;
    }

    vector<pair<int, int>> edges;
    for (int u = 0; u < n; ++u)
        for (int v : adj[u])
            if (u < v)
                edges.emplace_back(u, v);

    sort(edges.begin(), edges.end());
    int cur = 0;
    for (int u = 0; u < n; ++u) {
        for (int v = u + 1; v < n; ++v) {
            if (abs(labels[u] - labels[v]) <= 1) {
                if (cur >= (int)edges.size() || edges[cur] != make_pair(u, v)) {
                    possible = false;
                    break;
                }
                ++cur;
            }
        }
        if (!possible) break;
    }

    if (!possible) {
        cout << "NO\n";
        return 0;
    }

    cout << "YES\n";
    for (int i = 0; i < n; ++i)
        cout << labels[i] + 1 << " ";
    cout << "\n";

    return 0;
}

