    sort(strs.begin(), strs.end(), [](const string& a, const string& b) {
        return a.size() > b.size();
    });

    auto root = make_unique<TrieNode>();
    for (const auto& s : strs) {
        insertPrefixes(root.get(), s);
    }

    int total = 0;
    for (const auto& s : strs) {
        TrieNode* node = root.get();
        int len = 0;
        for (char c : s) {
            int idx = c - 'a';
            node = node->children[idx].get();
            len++;
            if (!node->used) {
                node->used = true;
                break;
            }
        }
        total += len;
    }

    cout << total << '\n';

    return 0;
}

