typedef long long ll;

int main() {
    int n, k, c;
    cin >> n >> k >> c;

    vector<ll> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    ll l, h;
    cin >> l >> h;

    ll ans = 0;
    for (int i = 0; i < (1 << (n - 1)); ++i) {
        if (__builtin_popcount(i) != k - 1) continue;

        vector<ll> parts;
        ll current_sum = 0;
        int start = 0;

        for (int j = 0; j < n - 1; ++j) {
            current_sum += (ll)pow(c, j - start) * a[j];
            if ((i >> j) & 1) {
                current_sum += (ll)pow(c, j - start + 1) * a[j+1];
                bool valid = true;
                ll part_sum = 0;
                 for(int idx = 0; idx <= j - start; idx++){
                    part_sum += (ll)pow(c, idx) * a[start + idx];
                 }

                if (part_sum < l || part_sum > h) {
                    valid = false;
                    break;
                }
                parts.emplace_back(part_sum);

                start = j + 1;
                current_sum = 0;
            }
        }
        ll final_sum = 0;
        for(int idx = start; idx < n; idx++){
            final_sum += (ll)pow(c, idx - start) * a[idx];
        }

        bool last_valid = (final_sum >= l && final_sum <= h);

        if(last_valid){
            parts.emplace_back(final_sum);
        }

        if (last_valid && parts.size() == k) {
            ans++;
        }
    }

    cout << ans % (1000000000 + 7) << '\n';

    return 0;
}

