#include <bits/stdc++.h>
using namespace std;
int h, w, n;
vector<string> grid;
bool isValid(int r, int c) {
    return r >= 0 && r < h && c >= 0 && c < w && grid[r][c] == '.';
}
bool solve(int a, int b, int c, int d, int l) {
    vector<vector<bool>> visited(h, vector<bool>(w, false));
    queue<pair<int, int>> q;
    q.emplace(a, b);
    visited[a][b] = true;
    while (!q.empty()) {
        int r = q.front().first;
        int col = q.front().second;
        q.pop();
        if (r == c && col == d) {
            return true;
        }
        int minRow = max(0, r - l);
        int maxRow = min(h - 1, r + l);
        int dr[] = {0, 0, 1, -1};
        int dc[] = {1, -1, 0, 0};
        for (int i = 0; i < 4; i++) {
            int newR = r + dr[i];
            int newC = col + dc[i];
            if (isValid(newR, newC) && !visited[newR][newC]) {
                bool valid = true;
                for (int j = 0; j < h; j++) {
                    if (j < minRow || j > maxRow) {
                        if (visited[j][0] || visited[j][w-1]){
                            valid = false;
                        }
                        for (int k=0; k<w;k++){
                            if (visited[j][k]){
                                valid = false;
                            }
                        }
                    }
                }
                if (valid) {
                    q.emplace(newR, newC);
                    visited[newR][newC] = true;
                }
            }
        }
    }
    return false;
}
int main() {
    cin >> h >> w >> n;
    grid.resize(h);
    for (int i = 0; i < h; i++) {
        cin >> grid[i];
    }
    for (int i = 0; i < n; i++) {
        int a, b, c, d, l;
        cin >> a >> b >> c >> d >> l;
        if (solve(a - 1, b - 1, c - 1, d - 1, l)) {
            cout << 1 << '\n';
        } else {
            cout << 0 << '\n';
        }
    }
    return 0;
}

