int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;
    vector<string> grid(n);
    for (auto& row : grid) cin >> row;

    vector<int> left_row(n, -1), right_row(n, -1);
    vector<int> top_col(m, -1), bottom_col(m, -1);

    // Check rows for contiguous and compute left/right
    for (int i = 0; i < n; ++i) {
        int l = -1, r = -1;
        for (int j = 0; j < m; ++j) {
            if (grid[i][j] == '#') {
                if (l == -1) l = j;
                r = j;
            }
        }
        if (l == -1) continue;
        left_row[i] = l;
        right_row[i] = r;
        // Check contiguous
        for (int j = l; j <= r; ++j) {
            if (grid[i][j] != '#') {
                cout << -1 << '\n';
                return 0;
            }
        }
    }

    // Check columns for contiguous and compute top/bottom
    for (int j = 0; j < m; ++j) {
        int t = -1, b = -1;
        for (int i = 0; i < n; ++i) {
            if (grid[i][j] == '#') {
                if (t == -1) t = i;
                b = i;
            }
        }
        if (t == -1) continue;
        top_col[j] = t;
        bottom_col[j] = b;
        // Check contiguous
        for (int i = t; i <= b; ++i) {
            if (grid[i][j] != '#') {
                cout << -1 << '\n';
                return 0;
            }
        }
    }

    // Check all-white rows and columns
    for (int i = 0; i < n; ++i) {
        if (left_row[i] == -1) {
            // Check all columns in this row are white
            for (int j = 0; j < m; ++j) {
                if (top_col[j] != -1) {
                    cout << -1 << '\n';
                    return 0;
                }
            }
        }
    }
    for (int j = 0; j < m; ++j) {
        if (top_col[j] == -1) {
            // Check all rows in this column are white
            for (int i = 0; i < n; ++i) {
                if (left_row[i] != -1) {
                    cout << -1 << '\n';
                    return 0;
                }
            }
        }
    }

    // Compute connected components
    vector<bool> visited_row(n, false), visited_col(m, false);
    int components = 0;

    for (int i = 0; i < n; ++i) {
        if (left_row[i] != -1 && !visited_row[i]) {
            ++components;
            queue<pair<bool, int>> q;
            q.push({true, i});
            while (!q.empty()) {
                auto [is_row, idx] = q.front();
                q.pop();
                if (is_row) {
                    if (visited_row[idx]) continue;
                    visited_row[idx] = true;
                    int l = left_row[idx], r = right_row[idx];
                    for (int j = l; j <= r; ++j) {
                        if (!visited_col[j]) {
                            q.push({false, j});
                        }
                    }
                } else {
                    if (visited_col[idx]) continue;
                    visited_col[idx] = true;
                    int t = top_col[idx], b = bottom_col[idx];
                    for (int i2 = t; i2 <= b; ++i2) {
                        if (!visited_row[i2]) {
                            q.push({true, i2});
                        }
                    }
                }
            }
        }
    }

    cout << components << '\n';
    return 0;
}

