vector<int> Q; // Q[d] is the initial position of dancer d
int N;
int current_shift;
bool inverted;

void init(int _N, vector<int> P) {
    N = _N;
    Q.resize(N);
    for (int i = 0; i < N; ++i) {
        Q[P[i]] = i;
    }
    current_shift = 0;
    inverted = false;
}

void move_right(int K) {
    if (!inverted) {
        current_shift = (current_shift + K) % N;
    } else {
        current_shift = (current_shift - K + N) % N;
    }
}

void move_left(int K) {
    move_right((N - K) % N);
}

void swap_places() {
    // Not used as per constraints
}

void move_around() {
    inverted = !inverted;
    current_shift = (N - current_shift) % N;
}

int get_position(int D) {
    int pos;
    if (!inverted) {
        pos = (Q[D] + current_shift) % N;
    } else {
        pos = (current_shift - Q[D] + N) % N;
    }
    return pos;
}

