void program_pulibot() {
    // End condition: current cell is 0, south and east are boundaries
    for (int s1 : {-2, -1, 0}) {
        for (int s4 : {-2, -1, 0}) {
            set_instruction({0, s1, -2, -2, s4}, 1, 'T');
        }
    }

    // Priority: East -> South -> West -> North
    // East first
    for (int s1 : {-2, -1, 0}) {
        for (int s2 : {-2, -1, 0}) {
            for (int s4 : {-2, -1, 0}) {
                set_instruction({0, s1, s2, 0, s4}, 1, 'E');
            }
        }
    }

    // South next (east is not 0)
    for (int s1 : {-2, -1, 0}) {
        for (int s3 : {-2, -1}) {
            for (int s4 : {-2, -1, 0}) {
                set_instruction({0, s1, 0, s3, s4}, 1, 'S');
            }
        }
    }

    // West next (east and south not 0)
    for (int s2 : {-2, -1}) {
        for (int s3 : {-2, -1}) {
            for (int s4 : {-2, -1, 0}) {
                set_instruction({0, 0, s2, s3, s4}, 1, 'W');
            }
        }
    }

    // North next (east, south, west not 0)
    for (int s1 : {-2, -1}) {
        for (int s2 : {-2, -1}) {
            for (int s3 : {-2, -1}) {
                set_instruction({0, s1, s2, s3, 0}, 1, 'N');
            }
        }
    }
}

