int find_maximum_unique(int x, int y, vector<int> a, vector<int> b) {
    int n = a.size();
    vector<vector<int>> dp(x + 1, vector<int>(y + 1, -1));
    dp[0][0] = 0;

    for (int k = 0; k < n; ++k) {
        vector<vector<int>> temp = dp;
        for (int i = 0; i <= x; ++i) {
            for (int j = 0; j <= y; ++j) {
                if (dp[i][j] == -1) continue;
                // Try adding to Store A
                int ni = i + a[k];
                int nj = j;
                if (ni <= x) {
                    if (temp[ni][nj] < dp[i][j] + 1) {
                        temp[ni][nj] = dp[i][j] + 1;
                    }
                }
                // Try adding to Store B
                ni = i;
                nj = j + b[k];
                if (nj <= y) {
                    if (temp[ni][nj] < dp[i][j] + 1) {
                        temp[ni][nj] = dp[i][j] + 1;
                    }
                }
            }
        }
        dp = move(temp);
    }

    int max_count = 0;
    for (int i = 0; i <= x; ++i) {
        for (int j = 0; j <= y; ++j) {
            max_count = max(max_count, dp[i][j]);
        }
    }
    return max_count;
}

