struct Event {
    int time;
    bool is_start;
    int user;

    Event(int t, bool s, int u) : time(t), is_start(s), user(u) {}

    bool operator < (const Event& other) const {
        if (time != other.time) return time < other.time;
        return is_start && !other.is_start;
    }
};

class UnionFind {
private:
    vector<int> parent;
    vector<int> sz;

public:
    UnionFind(int n) {
        parent.resize(n);
        sz.resize(n, 1);
        for (int i = 0; i < n; ++i)
            parent[i] = i;
    }

    int find(int x) {
        if (parent[x] != x)
            parent[x] = find(parent[x]);
        return parent[x];
    }

    void unite(int x, int y) {
        x = find(x);
        y = find(y);
        if (x == y) return;
        if (sz[x] < sz[y]) swap(x, y);
        parent[y] = x;
        sz[x] += sz[y];
    }

    int size(int x) {
        return sz[find(x)];
    }
};

UnionFind *uf;

void init(int N, int S, vector<int> T,
          vector<vector<int>> A, vector<vector<int>> B) {

    vector<Event> events;

    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < T[i]; ++j) {
            int a = A[i][j];
            int b = B[i][j];
            events.emplace_back(a, true, i);
            events.emplace_back(b + 1, false, i);
        }
    }

    sort(events.begin(), events.end());

    uf = new UnionFind(N);

    unordered_set<int> active_set;

    for (const auto& event : events) {
        int u = event.user;
        if (event.is_start) {
            if (!active_set.empty()) {
                int v = *active_set.begin();
                uf->unite(u, v);
            }
            active_set.insert(u);
        } else {
            active_set.erase(u);
        }
    }
}

int count_users(int P) {
    return uf->size(P);
}

#include <bits/stdc++.h>
using namespace std;

const int MAXN = 2e5 + 5;
const int LOG = 20;

vector<int> adj[MAXN];
int depth[MAXN], up[MAXN][LOG];
int tin[MAXN], tout[MAXN], timer = 0;
int value[MAXN];
int visited[MAXN];
int current_time = 1;

void dfs(int u, int parent) {
    tin[u] = ++timer;
    up[u][0] = parent;
    for (int i = 1; i < LOG; ++i)
        up[u][i] = up[up[u][i-1]][i-1];
    for (int v : adj[u]) {
        if (v != parent) {
            depth[v] = depth[u] + 1;
            dfs(v, u);
        }
    }
    tout[u] = ++timer;
}

bool is_ancestor(int u, int v) {
    return tin[u] <= tin[v] && tout[u] >= tout[v];
}

int lca(int u, int v) {
    if (is_ancestor(u, v)) return u;
    if (is_ancestor(v, u)) return v;
    for (int i = LOG-1; i >= 0; --i)
        if (!is_ancestor(up[u][i], v))
            u = up[u][i];
    return up[u][0];
}

vector<int> get_path(int u, int v) {
    int ancestor = lca(u, v);
    vector<int> path;
    while (u != ancestor) {
        path.push_back(u);
        u = up[u][0];
    }
    path.push_back(ancestor);
    vector<int> temp;
    while (v != ancestor) {
        temp.push_back(v);
        v = up[v][0];
    }
    reverse(temp.begin(), temp.end());
    path.insert(path.end(), temp.begin(), temp.end());
    return path;
}

