int find_town(int N) {
    if (N == 1) return 0; // Edge case: only one town

    int candidate = 0;
    int outgoing_count = 0;

    // Initial candidate selection
    for (int i = 1; i < N; ++i) {
        if (check_road(candidate, i)) {
            outgoing_count++;
            if (outgoing_count > 1) {
                candidate = i;
                outgoing_count = 0;
            }
        }
    }

    // Verify the candidate's out-degree
    int out_degree = 0;
    for (int i = 0; i < N; ++i) {
        if (i == candidate) continue;
        if (check_road(candidate, i)) {
            out_degree++;
            if (out_degree > 1) {
                break; // Early exit if exceeds 1
            }
        }
    }

    if (out_degree <= 1) {
        return candidate;
    }

    // If candidate is invalid, check the last town that caused the candidate to change (if any)
    // Alternatively, return -1
    return -1;
}

