    vector<int> dp(n + 2, 0);
    dp[0] = 1;

    for (int x : a) {
        vector<int> divisors;
        for (int i = 1; i * i <= x; ++i) {
            if (x % i == 0) {
                if (i <= n) divisors.push_back(i);
                int j = x / i;
                if (j <= n) divisors.push_back(j);
            }
        }
        sort(divisors.begin(), divisors.end());
        auto last = unique(divisors.begin(), divisors.end());
        divisors.erase(last, divisors.end());
        sort(divisors.rbegin(), divisors.rend());

        for (int k : divisors) {
            dp[k] = (dp[k] + dp[k - 1]) % MOD;
        }
    }

    int ans = 0;
    for (int i = 1; i <= n; ++i) {
        ans = (ans + dp[i]) % MOD;
    }
    cout << ans << '\n';

    return 0;
}

