vector<vector<int>> devise_strategy(int N) {
    vector<vector<int>> s(2, vector<int>(N + 1, 0));

    // State 0: check A
    s[0][0] = 0; // Inspect A
    s[0][1] = -1; // If A has 1, declare A
    s[0][N] = -2; // If A has N, declare B
    for (int j = 2; j <= N-1; ++j) {
        s[0][j] = 1; // Transition to state 1
    }

    // State 1: check B
    s[1][0] = 1; // Inspect B
    s[1][1] = -2; // If B has 1, declare B
    s[1][N] = -1; // If B has N, declare A
    for (int j = 2; j <= N-1; ++j) {
        s[1][j] = 0; // Transition to state 0
    }

    return s;
}

