using namespace std;

struct Person {
    int ti, si, fi, idx;
    bool operator<(const Person &other) const {
        return ti < other.ti;
    }
};

class FenwickTree {
private:
    vector<int> data;
    int size;

public:
    FenwickTree(int n) : data(n + 1, 0), size(n) {}

    void add(int idx, int delta) {
        for (; idx <= size; idx += idx & -idx) {
            data[idx] += delta;
        }
    }

    int query(int idx) {
        int res = 0;
        for (; idx > 0; idx -= idx & -idx) {
            res += data[idx];
        }
        return res;
    }

    int query(int l, int r) {
        if (l > r) return 0;
        return query(r) - query(l - 1);
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;

    vector<Person> people(n);
    for (int i = 0; i < n; ++i) {
        cin >> people[i].ti >> people[i].si >> people[i].fi;
        people[i].idx = i;
    }

    sort(people.begin(), people.end());

    FenwickTree waiting_fenwick(m);
    FenwickTree passengers_fenwick(m);
    unordered_map<int, vector<int>> passengers_map;
    vector<queue<int>> waiting_queues(m + 1); // 1-based indexing for floors
    vector<int> ans(n);

    int current_time = 0;
    int current_floor = 1;
    int i = 0;

    while (i < n || passengers_fenwick.query(1, m) > 0 || waiting_fenwick.query(1, m) > 0) {
        // Process all arrivals up to current_time
        while (i < n && people[i].ti <= current_time) {
            const Person &p = people[i];
            waiting_queues[p.si].push(i);
            waiting_fenwick.add(p.si, 1);
            ++i;
        }

        int up_waiting = waiting_fenwick.query(current_floor + 1, m);
        int down_waiting = waiting_fenwick.query(1, current_floor - 1);
        int up_passengers = passengers_fenwick.query(current_floor + 1, m);
        int down_passengers = passengers_fenwick.query(1, current_floor - 1);
        int pup = up_waiting + up_passengers;
        int pdown = down_waiting + down_passengers;

        if (pup + pdown == 0) {
            if (i < n) {
                current_time = people[i].ti;
            } else {
                break;
            }
        } else {
            // Process current floor: drop off passengers
            auto it = passengers_map.find(current_floor);
            if (it != passengers_map.end()) {
                for (int idx : it->second) {
                    ans[people[idx].idx] = current_time;
                }
                passengers_fenwick.add(current_floor, -it->second.size());
                passengers_map.erase(it);
            }

            // Process current floor: pick up waiting people
            queue<int> &q = waiting_queues[current_floor];
            if (!q.empty()) {
                int cnt = q.size();
                while (!q.empty()) {
                    int idx = q.front();
                    q.pop();
                    const Person &p = people[idx];
                    passengers_fenwick.add(p.fi, 1);
                    passengers_map[p.fi].push_back(idx);
                }
                waiting_fenwick.add(current_floor, -cnt);
            }

            // Determine next direction
            up_waiting = waiting_fenwick.query(current_floor + 1, m);
            down_waiting = waiting_fenwick.query(1, current_floor - 1);
            up_passengers = passengers_fenwick.query(current_floor + 1, m);
            down_passengers = passengers_fenwick.query(1, current_floor - 1);
            pup = up_waiting + up_passengers;
            pdown = down_waiting + down_passengers;

            if (pup >= pdown) {
                current_floor = min(current_floor + 1, m);
            } else {
                current_floor = max(current_floor - 1, 1);
            }
            ++current_time;
        }
    }

    for (int time : ans) {
        cout << time << '\n';
    }

    return 0;
}

