const int MOD = 1e9+7;

vector<int> compute_occurrences(const vector<int>& s, int f) {
    vector<int> res;
    for (int i = 0; i < s.size(); ++i) {
        if (s[i] == f) res.push_back(i);
    }
    return res;
}

vector<int> reverse_occurrences(const vector<int>& s, int f) {
    vector<int> res;
    for (int i = s.size()-1; i >= 0; --i) {
        if (s[i] == f) res.push_back(s.size()-1 - i);
    }
    return res;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int n, m;
    cin >> n >> m;
    vector<int> s(n);
    for (int& x : s) cin >> x;

    vector<tuple<int, int, int, int>> cows(m);
    for (int i = 0; i < m; ++i) {
        int f, h;
        cin >> f >> h;
        auto occ = compute_occurrences(s, f);
        if (occ.size() < h) {
            cows[i] = {-1, -1, f, h};
            continue;
        }
        int left_pos = occ[h-1];
        auto rocc = reverse_occurrences(s, f);
        if (rocc.size() < h) {
            cows[i] = {left_pos, -1, f, h};
        } else {
            int right_pos = s.size() - 1 - rocc[h-1];
            cows[i] = {left_pos, right_pos, f, h};
        }
    }

    vector<int> cnt(n+1, 0);
    for (int x : s) cnt[x]++;

    vector<map<int, vector<int>>> left(n+1), right(n+1);
    for (auto& [lp, rp, f, h] : cows) {
        if (lp != -1) {
            left[f][lp].push_back(h);
        }
        if (rp != -1) {
            right[f][rp].push_back(h);
        }
    }

    int max_total = 0;
    ll total_ways = 0;

    for (int k = -1; k < n; ++k) {
        vector<int> combined;
        ll ways = 1;
        int current_total = 0;

        for (int f = 1; f <= n; ++f) {
            vector<int> L, R;
            for (auto& [pos, hs] : left[f]) {
                if (pos <= k) {
                    L.insert(L.end(), hs.begin(), hs.end());
                }
            }
            for (auto& [pos, hs] : right[f]) {
                if (pos > k) {
                    R.insert(R.end(), hs.begin(), hs.end());
                }
            }
            vector<int> all;
            all.insert(all.end(), L.begin(), L.end());
            all.insert(all.end(), R.begin(), R.end());
            sort(all.begin(), all.end());

            vector<int> pre_sum(all.size() + 1, 0);
            for (int i = 0; i < all.size(); ++i)
                pre_sum[i+1] = pre_sum[i] + all[i];

            int t = 0;
            while (t < all.size() && pre_sum[t+1] <= cnt[f]) t++;

            if (t == 0) continue;
            current_total += t;

            ll cnt_ways = 0;
            int max_t = t;
            int sum_needed = pre_sum[max_t];
            if (sum_needed <= cnt[f]) {
                cnt_ways = 1;
                for (int i = 0; i < max_t; ++i) {
                    if (all[i] == all[max_t-1]) {
                        int same = count(all.begin(), all.end(), all[i]);
                        int choose = max_t - i;
                        cnt_ways = 1;
                        break;
                    }
                }
            }

            if (cnt_ways == 0) {
                ways = 0;
                break;
            }
            ways = (ways * cnt_ways) % MOD;
        }

        if (current_total > max_total) {
            max_total = current_total;
            total_ways = ways;
        } else if (current_total == max_total) {
            total_ways = (total_ways + ways) % MOD;
        }
    }

    if (max_total == 0) total_ways = 1;
    cout << max_total << " " << total_ways << endl;
}

