long long kadane(const vector<long long>& a, int start, int end) {
    if (start > end) return LLONG_MIN;
    long long current_max = a[start];
    long long max_so_far = current_max;
    for (int i = start + 1; i <= end; ++i) {
        current_max = max(a[i], current_max + a[i]);
        max_so_far = max(max_so_far, current_max);
    }
    return max_so_far;
}

